package com.job.modelClass;

public class Employee {
	

private int id;
private String name;
private String gender;
private String address;
private String phoneNo;
private int no_of_days;
private int wages;
private int ProId;
private int GPMId;


public Employee() {
	// TODO Auto-generated constructor stub
}


public int getId() {
	return id;
}


public void setId(int id) {
	this.id = id;
}


public String getName() {
	return name;
}


public void setName(String name) {
	this.name = name;
}


public String getGender() {
	return gender;
}


public void setGender(String gender) {
	this.gender = gender;
}


public String getAddress() {
	return address;
}


public void setAddress(String address) {
	this.address = address;
}


public String getPhoneNo() {
	return phoneNo;
}


public void setPhoneNo(String phoneNo) {
	this.phoneNo = phoneNo;
}


public int getNo_of_days() {
	return no_of_days;
}


public void setNo_of_days(int no_of_days) {
	this.no_of_days = no_of_days;
}


public int getWages() {
	return wages;
}


public void setWages(int wages) {
	this.wages = wages;
}


public int getProId() {
	return ProId;
}


public void setProId(int proId) {
	ProId = proId;
}


public int getGPMId() {
	return GPMId;
}


public void setGPMId(int gPMId) {
	GPMId = gPMId;
}


public Employee(String name, String gender, String address, String phoneNo, int no_of_days, int wages, int proId,
		int gPMId) {
	super();
	this.name = name;
	this.gender = gender;
	this.address = address;
	this.phoneNo = phoneNo;
	this.no_of_days = no_of_days;
	this.wages = wages;
	ProId = proId;
	GPMId = gPMId;
}


@Override
public String toString() {
	return "Employee [id=" + id + ", name=" + name + ", gender=" + gender + ", address=" + address + ", phoneNo="
			+ phoneNo + ", no_of_days=" + no_of_days + ", wages=" + wages + ", ProId=" + ProId + ", GPMId=" + GPMId
			+ "]";
}




}
